import { createSlice, Slice } from '@reduxjs/toolkit';
import axios from 'axios';
import { PROTOCOL, SERVER_HOST, SERVER_PORT } from '../../config';

export const userSlice: Slice = createSlice({
  name: 'users',
  initialState: {
    currentUser: {},
  },
  reducers: {
    signup: (state, action) => {
      const url: string = `${PROTOCOL}://${SERVER_HOST}:${SERVER_PORT}/users/signup`;
      axios.post(url, action.payload, { 
        headers: {
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": url,
        },
      }).then(response => {
        const { token, createdUser }: {token: string, createdUser: object} = response.data;
        console.log(response);
        localStorage.setItem('token', token);
        state.currentUser = createdUser;
      });
    },
    signin: (state, action) => {
      axios.post(`${PROTOCOL}//${SERVER_HOST}:${SERVER_PORT}/users/signin`, action.payload).then(response => {
        console.log(response);
      });
    },
    signout: (state) => {
      state.currentUser = {};
      localStorage.removeItem('token');
    }
  },
});

export const { signup, signin, signout } = userSlice.actions;

export default userSlice.reducer;
