import { createSlice } from '@reduxjs/toolkit';
import axios from 'axios';
import { PROTOCOL, SERVER_HOST, SERVER_PORT } from '../../config';

export const userSlice = createSlice({
  name: 'users',
  initialState: {
    currentUser: {},
  },
  reducers: {
    signup: (state, action) => {
      const url = `${PROTOCOL}://${SERVER_HOST}:${SERVER_PORT}/users/signup`;
      axios.post(url, action.payload, { 
        headers: {
          "Content-Type": "application/json",
          "Access-Control-Allow-Origin": url,
        },
      }).then(response => {
        console.log(response);
        localStorage.setItem('token', response.data.token);
      });
    },
    signin: (state, action) => {
      axios.post(`${PROTOCOL}//${SERVER_HOST}:${SERVER_PORT}/users/signin`, action.payload).then(response => {
        console.log(response);
      });
    },
    signout: () => {
      localStorage.removeItem('token');
    }
  },
});

export const { signup, signin } = userSlice.actions;

export default userSlice.reducer;
