import React from 'react';
import { Button } from '@mui/material';
import styled from 'styled-components';
import { CreateItem } from './CreateItem';
import { addDesk } from '../store/reducers/deskReducer';
import { useAppSelector, useAppDispatch } from '../store/hooks';

export const ProfileDesks: React.FC = () => {
  const desks = useAppSelector(state => state.desks.desks);
  const dispatch = useAppDispatch();

  const addClickHandler = () => {
    dispatch(addDesk({title: 'Test!'}));
  };

  return (
    <StyledContainer>{desks.map((desk: { title: string; })=> {
      return (
        <CreateItem 
          title={desk.title}
        />
      );
    })}
      <StyledButton 
        onClick={addClickHandler}
      >
        Create desk!
      </StyledButton>
    </StyledContainer>
  )
};


const StyledContainer = styled.div`
  display: flex;
  justify-content: flex-start;
  flex-direction: row;
  flex-wrap: wrap;
  padding: 30px;
`;

const StyledButton = styled.button`
  margin: 5px;
  width: 100px;
  height: 50px;
  max-width: 100px;
  font-size: 15px;
  border: 2px dashed #00bfff;
  background-color: #1faee9;
  border-radius: 5px;
  padding: 0;
`;
