      <Column title='hopa' id='c228'/>
      <Column title='hopa' id='c228'/>
      <Column title='hopa' id='c228'/>
    </StyledColumnsWrapper>
  </>
  )};


const StyledColumnsWrapper = styled.div`
  display: inline-flex;
  width: 100%;
  overflow-x: auto;
`;
