import { Navigate, Route, RoutesProps, useLocation } from "react-router"
import store from "../store/store";

interface Props {
  path: string;
  element: JSX.Element;
}

export const RequireAuth = ({...props}: Props) => {
  const location = useLocation();
  // const isAuth = false;
  const isAuth = store.getState().users['isAuth'];

  if (isAuth) {
    return <Route {...props} /> 
  }
  return <Navigate to='/signin' state={ {from: location} } />;
}
